import React, { useEffect } from 'react';
import '../style/AutoSlideLoop.css'; // Create and import a separate CSS file for styling

export const Teams = () => {
  useEffect(() => {
    const logosSlide = document.querySelector('.logos-slide');
    const copy = logosSlide.cloneNode(true);
    document.querySelector('.logos').appendChild(copy);
  }, []);

  const logoItems = [
    { src: './logos/3m.svg', title: '3M' },
    { src: './logos/barstool-store.svg', title: 'Barstool Store' },
    { src: './logos/budweiser.svg', title: 'Budweiser' },
    { src: './logos/buzzfeed.svg', title: 'BuzzFeed' },
    { src: './logos/forbes.svg', title: 'Forbes' },
    { src: './logos/macys.svg', title: 'Macy\'s' },
    { src: './logos/menshealth.svg', title: 'Men\'s Health' },
    { src: './logos/mrbeast.svg', title: 'MrBeast' },
  ];

  return (
    <div className="logos">
      <div className="logos-slide">
        {logoItems.map((item, index) => (
          <div key={index} className="logo-item">
            <img src={item.src} alt={item.title} />
            <h3>{item.title}</h3>
            <button>See More</button>
          </div>
        ))}
      </div>
    </div>
  );
};
