import React, { useState, useRef } from 'react';
import { pictures } from '../data/tifo';
import '../style/Tifo.css';

export const Tifo = () => {
  const [selectedPicture, setSelectedPicture] = useState(null);
  const [modalPosition, setModalPosition] = useState({});
  const buttonRefs = useRef([]);

  const handleClick = (picture, index) => {
    const buttonRect = buttonRefs.current[index].getBoundingClientRect();
    setModalPosition({
      top: buttonRect.bottom + window.scrollY,
      left: buttonRect.left + window.scrollX,
    });
    setSelectedPicture(picture);
  };

  const handleClose = () => {
    setSelectedPicture(null);
  };

  return (
    <div className="card-container">
      {pictures.map((picture, index) => (
        <button
          onClick={() => handleClick(picture, index)}
          key={picture.id}
          ref={el => buttonRefs.current[index] = el}
        >
          <img src={picture.imageUrl} className="card-img" alt={picture.title} />
        </button>
      ))}

      {selectedPicture && (
        <div className="modal" style={{ top: modalPosition.top, left: modalPosition.left }}>
          <div className="modal-content">
            <span className="close" onClick={handleClose}>&times;</span>
            <img src={selectedPicture.imageUrl} alt={selectedPicture.title} />
            <p>{selectedPicture.title}</p>
          </div>
        </div>
      )}
    </div>
  );
};
